import React, { useState } from 'react';
import { Calendar, Clock, Video, Phone, User, Mail, Building, MessageSquare, Check } from 'lucide-react';

const Appointment = () => {
  // ============ ÉTATS DE GESTION DU COMPOSANT ============
  
  // États pour la sélection de date et heure
  const [selectedDate, setSelectedDate] = useState(''); // Date sélectionnée (format YYYY-MM-DD)
  const [selectedTime, setSelectedTime] = useState(''); // Heure sélectionnée
  const [consultationType, setConsultationType] = useState('video'); // Type : 'video' ou 'phone'
  const [currentStep, setCurrentStep] = useState(1); // Étape actuelle (1, 2, ou 3)
  
  // État pour les données du formulaire
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    company: '',
    requirements: ''
  });
  
  // État pour afficher la confirmation
  const [showConfirmation, setShowConfirmation] = useState(false);

  // ============ GESTION DU CALENDRIER ============
  
  // États pour la navigation dans le calendrier
  const currentMonth = new Date().getMonth();
  const currentYear = new Date().getFullYear();
  const [viewMonth, setViewMonth] = useState(currentMonth); // Mois affiché
  const [viewYear, setViewYear] = useState(currentYear); // Année affichée

  // Noms des mois en français
  const monthNames = [
    'Janvier', 'Février', 'Mars', 'Avril', 'Mai', 'Juin',
    'Juillet', 'Août', 'Septembre', 'Octobre', 'Novembre', 'Décembre'
  ];

  // Créneaux horaires disponibles
  const timeSlots = [
    '09:00', '09:30', '10:00', '10:30', '11:00', '11:30',
    '14:00', '14:30', '15:00', '15:30', '16:00', '16:30'
  ];

  // ============ FONCTIONS UTILITAIRES POUR LE CALENDRIER ============
  
  // Obtient le nombre de jours dans un mois donné
  const getDaysInMonth = (month, year) => {
    return new Date(year, month + 1, 0).getDate();
  };

  // Obtient le premier jour du mois (0 = dimanche, 1 = lundi, etc.)
  const getFirstDayOfMonth = (month, year) => {
    return new Date(year, month, 1).getDay();
  };

  // Vérifie si une date est disponible pour réservation
  const isDateAvailable = (date) => {
    const today = new Date();
    const checkDate = new Date(viewYear, viewMonth, date);
    // Disponible si : date >= aujourd'hui ET pas weekend (dimanche=0, samedi=6)
    return checkDate >= today && checkDate.getDay() !== 0 && checkDate.getDay() !== 6;
  };

  // ============ GESTIONNAIRES D'ÉVÉNEMENTS ============
  
  // Gère la sélection d'une date
  const handleDateSelect = (date) => {
    if (isDateAvailable(date)) {
      // Formate la date au format YYYY-MM-DD
      const dateStr = `${viewYear}-${String(viewMonth + 1).padStart(2, '0')}-${String(date).padStart(2, '0')}`;
      setSelectedDate(dateStr);
      setCurrentStep(2); // Passe à l'étape 2 (sélection heure)
    }
  };

  // Gère la sélection d'un créneau horaire
  const handleTimeSelect = (time) => {
    setSelectedTime(time);
    setCurrentStep(3); // Passe à l'étape 3 (formulaire)
  };

  // Gère la soumission du formulaire
  const handleFormSubmit = () => {
    // Vérifie que les champs obligatoires sont remplis
    if (formData.fullName && formData.email && formData.phone) {
      setShowConfirmation(true); // Affiche la confirmation
      
      // ⚠️ POINT IMPORTANT : ICI IL FAUDRAIT SAUVEGARDER LES DONNÉES
      // Exemple d'appel API qu'il faudrait ajouter :
      /*
      const appointmentData = {
        date: selectedDate,
        time: selectedTime,
        type: consultationType,
        ...formData
      };
      
      fetch('/api/appointments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(appointmentData)
      })
      .then(response => response.json())
      .then(data => {
        console.log('Rendez-vous sauvegardé:', data);
        setShowConfirmation(true);
      })
      .catch(error => console.error('Erreur:', error));
      */
    }
  };

  // ============ FONCTIONS D'AFFICHAGE ============
  
  // Formate la date sélectionnée pour l'affichage
  const formatSelectedDate = () => {
    if (!selectedDate) return '';
    const date = new Date(selectedDate);
    return date.toLocaleDateString('fr-FR', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  // Génère la grille du calendrier
  const renderCalendar = () => {
    const daysInMonth = getDaysInMonth(viewMonth, viewYear);
    const firstDay = getFirstDayOfMonth(viewMonth, viewYear);
    const days = [];

    // Ajuste pour que lundi soit le premier jour (au lieu de dimanche)
    const adjustedFirstDay = firstDay === 0 ? 6 : firstDay - 1;

    // Ajoute des cellules vides pour les jours du mois précédent
    for (let i = 0; i < adjustedFirstDay; i++) {
      days.push(<div key={`empty-${i}`} className="calendar-day-empty"></div>);
    }

    // Ajoute les jours du mois courant
    for (let date = 1; date <= daysInMonth; date++) {
      const isAvailable = isDateAvailable(date);
      const isSelected = selectedDate === `${viewYear}-${String(viewMonth + 1).padStart(2, '0')}-${String(date).padStart(2, '0')}`;
      
      days.push(
        <button
          key={date}
          onClick={() => handleDateSelect(date)}
          disabled={!isAvailable}
          className={`calendar-day ${
            isSelected
              ? 'calendar-day-selected'
              : isAvailable
              ? 'calendar-day-available'
              : 'calendar-day-disabled'
          }`}
        >
          {date}
        </button>
      );
    }

    return days;
  };

  // ============ AFFICHAGE DE LA PAGE DE CONFIRMATION ============
  
  if (showConfirmation) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-12">
        <div className="max-w-2xl mx-auto px-4">
          <div className="bg-white rounded-2xl shadow-xl p-8 text-center">
            {/* Icône de confirmation */}
            <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-6">
              <Check size={40} className="text-green-600" />
            </div>
            
            {/* Titre de confirmation */}
            <h1 className="text-3xl font-bold text-gray-900 mb-4">Rendez-vous confirmé !</h1>
            <p className="text-gray-600 mb-8">
              Votre consultation EDI a été planifiée avec succès. Vous recevrez bientôt un email de confirmation avec tous les détails.
            </p>
            
            {/* Détails du rendez-vous */}
            <div className="bg-gray-50 rounded-xl p-6 mb-6">
              <h3 className="flex items-center justify-center gap-2 text-lg font-semibold text-gray-900 mb-4">
                <Calendar size={20} />
                Détails du rendez-vous
              </h3>
              <div className="space-y-3 text-left">
                <div className="flex justify-between">
                  <span className="font-medium text-gray-700">Date :</span>
                  <span className="text-gray-900">{formatSelectedDate()}</span>
                </div>
                <div className="flex justify-between">
                  <span className="font-medium text-gray-700">Heure :</span>
                  <span className="text-gray-900">{selectedTime}</span>
                </div>
                <div className="flex justify-between">
                  <span className="font-medium text-gray-700">Type :</span>
                  <span className="flex items-center gap-1 text-gray-900">
                    {consultationType === 'video' ? <Video size={16} /> : <Phone size={16} />}
                    {consultationType === 'video' ? 'Visioconférence' : 'Appel téléphonique'}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="font-medium text-gray-700">Durée :</span>
                  <span className="text-gray-900">45 minutes</span>
                </div>
              </div>
            </div>

            {/* Information email */}
            <div className="text-sm text-gray-600 mb-6">
              <p>Un email de confirmation a été envoyé à <strong>{formData.email}</strong></p>
              <p>Vous recevrez le lien de connexion 1 heure avant le rendez-vous.</p>
            </div>

            {/* Bouton retour */}
            <button 
              onClick={() => window.location.href = '/'}
              className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-8 rounded-lg transition-colors"
            >
              Retour à l'accueil
            </button>
          </div>
        </div>
      </div>
    );
  }

  // ============ AFFICHAGE PRINCIPAL DE L'INTERFACE DE RÉSERVATION ============
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-12">
      <div className="max-w-6xl mx-auto px-4">
        
        {/* En-tête de la page */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">Planifier votre consultation EDI</h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Réservez un créneau pour discuter de vos besoins en intégration de données électroniques et explorer les solutions adaptées à votre entreprise.
          </p>
        </div>

        {/* Indicateur de progression */}
        <div className="flex justify-center mb-12">
          <div className="flex items-center space-x-4">
            {[1, 2, 3].map((step, index) => (
              <React.Fragment key={step}>
                <div className={`w-10 h-10 rounded-full flex items-center justify-center font-semibold ${
                  currentStep >= step 
                    ? 'bg-blue-600 text-white' 
                    : 'bg-gray-200 text-gray-500'
                }`}>
                  {step}
                </div>
                {index < 2 && (
                  <div className={`w-16 h-1 ${
                    currentStep > step ? 'bg-blue-600' : 'bg-gray-200'
                  }`}></div>
                )}
              </React.Fragment>
            ))}
          </div>
        </div>

        <div className="grid lg:grid-cols-2 gap-8">
          
          {/* ============ SECTION CALENDRIER (COLONNE GAUCHE) ============ */}
          <div className="space-y-6">
            
            {/* Carte du calendrier */}
            <div className="bg-white rounded-2xl shadow-lg p-6">
              <div className="flex items-center justify-between mb-6">
                <h2 className="flex items-center gap-3 text-2xl font-semibold text-gray-900">
                  <Calendar size={28} />
                  Choisir une date
                </h2>
                <div className="text-sm text-gray-500">
                  Fuseau horaire : UTC+1 (Paris)
                </div>
              </div>

              {/* Navigation du calendrier */}
              <div className="flex items-center justify-between mb-6">
                <button
                  onClick={() => {
                    if (viewMonth === 0) {
                      setViewMonth(11);
                      setViewYear(viewYear - 1);
                    } else {
                      setViewMonth(viewMonth - 1);
                    }
                  }}
                  className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
                >
                  ←
                </button>
                <h3 className="text-xl font-semibold text-gray-900">
                  {monthNames[viewMonth]} {viewYear}
                </h3>
                <button
                  onClick={() => {
                    if (viewMonth === 11) {
                      setViewMonth(0);
                      setViewYear(viewYear + 1);
                    } else {
                      setViewMonth(viewMonth + 1);
                    }
                  }}
                  className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
                >
                  →
                </button>
              </div>

              {/* En-têtes des jours de la semaine */}
              <div className="grid grid-cols-7 gap-1 mb-2">
                {['Lun', 'Mar', 'Mer', 'Jeu', 'Ven', 'Sam', 'Dim'].map((day) => (
                  <div key={day} className="text-center text-sm font-medium text-gray-600 py-2">
                    {day}
                  </div>
                ))}
              </div>
              
              {/* Grille du calendrier */}
              <div className="grid grid-cols-7 gap-1 mb-4">
                {renderCalendar()}
              </div>

              {/* Légende */}
              <div className="flex items-center gap-6 text-sm">
                <div className="flex items-center gap-2">
                  <div className="w-4 h-4 bg-blue-600 rounded"></div>
                  Disponible
                </div>
                <div className="flex items-center gap-2">
                  <div className="w-4 h-4 bg-gray-300 rounded"></div>
                  Indisponible
                </div>
              </div>
            </div>

            {/* ============ SECTION CRÉNEAUX HORAIRES ============ */}
            {selectedDate && (
              <div className="bg-white rounded-2xl shadow-lg p-6">
                <h3 className="flex items-center gap-3 text-xl font-semibold text-gray-900 mb-6">
                  <Clock size={24} />
                  Créneaux disponibles - {formatSelectedDate()}
                </h3>
                <div className="grid grid-cols-3 gap-3">
                  {timeSlots.map((time) => (
                    <button
                      key={time}
                      onClick={() => handleTimeSelect(time)}
                      className={`p-3 rounded-lg border-2 transition-colors ${
                        selectedTime === time
                          ? 'border-blue-600 bg-blue-50 text-blue-900'
                          : 'border-gray-200 hover:border-blue-300 hover:bg-blue-50'
                      }`}
                    >
                      <div className="font-semibold">{time}</div>
                      <div className="text-sm text-gray-500">45 min</div>
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* ============ SECTION FORMULAIRE (COLONNE DROITE) ============ */}
          <div className="bg-white rounded-2xl shadow-lg p-6">
            <h3 className="text-2xl font-semibold text-gray-900 mb-6">Détails de la consultation</h3>

            {/* Choix du type de consultation */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-3">Type de consultation</label>
              <div className="grid grid-cols-2 gap-3">
                <label className="cursor-pointer">
                  <input
                    type="radio"
                    name="consultationType"
                    value="video"
                    checked={consultationType === 'video'}
                    onChange={(e) => setConsultationType(e.target.value)}
                    className="sr-only"
                  />
                  <div className={`p-4 rounded-lg border-2 flex items-center gap-3 transition-colors ${
                    consultationType === 'video'
                      ? 'border-blue-600 bg-blue-50 text-blue-900'
                      : 'border-gray-200 hover:border-blue-300'
                  }`}>
                    <Video size={20} />
                    <span className="font-medium">Visioconférence</span>
                  </div>
                </label>
                <label className="cursor-pointer">
                  <input
                    type="radio"
                    name="consultationType"
                    value="phone"
                    checked={consultationType === 'phone'}
                    onChange={(e) => setConsultationType(e.target.value)}
                    className="sr-only"
                  />
                  <div className={`p-4 rounded-lg border-2 flex items-center gap-3 transition-colors ${
                    consultationType === 'phone'
                      ? 'border-blue-600 bg-blue-50 text-blue-900'
                      : 'border-gray-200 hover:border-blue-300'
                  }`}>
                    <Phone size={20} />
                    <span className="font-medium">Téléphone</span>
                  </div>
                </label>
              </div>
            </div>

            {/* ============ FORMULAIRE DE CONTACT (ÉTAPE 3) ============ */}
            {currentStep >= 3 && (
              <div className="space-y-6">
                
                {/* Nom complet */}
                <div>
                  <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-2">
                    <User size={16} />
                    Nom complet *
                  </label>
                  <input
                    type="text"
                    required
                    value={formData.fullName}
                    onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Votre nom complet"
                  />
                </div>

                {/* Email */}
                <div>
                  <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-2">
                    <Mail size={16} />
                    Email *
                  </label>
                  <input
                    type="email"
                    required
                    value={formData.email}
                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="votre@email.com"
                  />
                </div>

                {/* Téléphone */}
                <div>
                  <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-2">
                    <Phone size={16} />
                    Téléphone *
                  </label>
                  <input
                    type="tel"
                    required
                    value={formData.phone}
                    onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="+33 1 23 45 67 89"
                  />
                </div>

                {/* Entreprise */}
                <div>
                  <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-2">
                    <Building size={16} />
                    Entreprise
                  </label>
                  <input
                    type="text"
                    value={formData.company}
                    onChange={(e) => setFormData({ ...formData, company: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Nom de votre entreprise"
                  />
                </div>

                {/* Besoins EDI */}
                <div>
                  <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-2">
                    <MessageSquare size={16} />
                    Besoins EDI
                  </label>
                  <textarea
                    rows={4}
                    value={formData.requirements}
                    onChange={(e) => setFormData({ ...formData, requirements: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                    placeholder="Décrivez brièvement vos besoins en EDI..."
                    maxLength={300}
                  />
                  <div className="text-sm text-gray-500 mt-1">
                    {formData.requirements.length}/300 caractères
                  </div>
                </div>

                {/* Bouton de confirmation */}
                <button
                  type="button"
                  onClick={handleFormSubmit}
                  disabled={!formData.fullName || !formData.email || !formData.phone}
                  className="w-full bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed text-white font-semibold py-4 px-6 rounded-lg transition-colors"
                >
                  Confirmer le rendez-vous
                </button>
              </div>
            )}

            {/* ============ RÉCAPITULATIF ============ */}
            {selectedDate && selectedTime && (
              <div className="mt-8 p-4 bg-gray-50 rounded-lg">
                <h4 className="font-semibold text-gray-900 mb-3">Récapitulatif</h4>
                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Date :</span>
                    <span className="font-medium">{formatSelectedDate()}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Heure :</span>
                    <span className="font-medium">{selectedTime}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Durée :</span>
                    <span className="font-medium">45 minutes</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Type :</span>
                    <span className="font-medium">
                      {consultationType === 'video' ? 'Visioconférence' : 'Téléphone'}
                    </span>
                  </div>
                </div>
              </div>
            )}

            {/* ============ POLITIQUE D'ANNULATION ============ */}
            <div className="mt-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
              <div className="text-sm text-yellow-800">
                <p className="font-medium mb-1">Reprogrammation : Possible jusqu'à 24h avant le RDV</p>
                <p className="font-medium">Annulation : Gratuite jusqu'à 2h avant le RDV</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Appointment;
